            return (
              <p key={i}>{fieldName} {formErrors[fieldName]}</p>
            )
          }
           axios.post(`http://localhost:8080/update/${this.state.email}`)
             .then(function(response){
               console.log(response);
               const modalSuccess = Modal.success();
               modalSuccess.update({
                title: 'User Updation',
                content: 'User Updated Successfully!',
              });
               //Perform action based on response
           })
             .catch(function(error){
               console.log(error);
               const modalFailure = Modal.error();
               modalFailure.update({
                title: 'User Updation',
                content: 'User Updation Failed!',
              });
               //Perform action based on error
             });
         }
       });
     }

    componentDidMount() {
      axios.get(`http://localhost:8080/user/${this.state.email}`)
        .then(res => {
          const user = res.data;
          this.setState({ user });
          console.log(user);
        })
    }

     render() {
       const { getFieldDecorator } = this.props.form;
       const { autoCompleteResult } = this.state;
       const formItemLayout = {
         labelCol: {
           xs: { span: 24 },
           sm: { span: 8 },
         },
         wrapperCol: {
           xs: { span: 24 },
           sm: { span: 16 },
         },
       };
       const config = {
         rules: [{ type: 'object', required: true, message: 'Please select birthdate!' }],
       };
       const tailFormItemLayout = {
         wrapperCol: {
           xs: {
             span: 24,
             offset: 0,
           },
           sm: {
             span: 16,
             offset: 8,
           },
         },
       };

       return (
         <Form onSubmit={this.sendLink}>
           <Form.Item
             {...formItemLayout}
             label="Email">
             {getFieldDecorator('email', {
               rules: [{
                 type: 'email', message: 'The input is not valid E-mail!',
               }, {
                 required: true, message: 'Please input your E-mail!',
               }]
             })(
               <Input style={{ width: '60%' }}/>
             )}
           </Form.Item>
           <Form.Item
             {...formItemLayout}
             label="Confirm Email"
           >
           {getFieldDecorator('confirmemail', {
             rules: [{
               type: 'email', message: 'The input is not valid E-mail!',
             }, {
               required: true, message: 'Please input your E-mail!',
             }]
           })(
            <Input style={{ width: '60%' }}/>
           )}
         </Form.Item>
           <Form.Item {...tailFormItemLayout}>
             <Button type="primary" htmlType="submit">Send Link</Button>
           </Form.Item>
         </Form>

       );
     }
   }

   const forgotPassword = Form.create({ name: 'forgot' })(ForgotPassword);

   export default forgotPassword;
